mapper.select:
select id, username, password
from noshowvaccine.user
where username = #{username}

select id, username, password
from noshowvaccine.user
where id = #{userId}

select id, username, password
from noshowvaccine.user
where username = #{username}
  and password = #{password}

mapper.update:
UPDATE noshowvaccine.user
SET password = #{password}
WHERE id = #{userId}
